package com.jsp.studyShare.Dao;import com.jsp.studyShare.model.Category;import org.apache.ibatis.annotations.Insert;import org.apache.ibatis.annotations.Mapper;import org.apache.ibatis.annotations.Select;import org.apache.ibatis.annotations.Update;import org.springframework.stereotype.Repository;import java.util.List;@Mapper@Repository("categoryDAO")public interface CategoryDao {    String TABLE_NAME = "category";    String INSERT_FIELD = "cname, status";    @Insert({"insert into",TABLE_NAME,"(",INSERT_FIELD,")values(#{cname},#{status})"})    void addCategory(String cname, int status);    @Update({"update", TABLE_NAME,"set status=#{status} where cid=#{cid}"})    void updateStatus(int cid, int status);    @Select({"select * from", TABLE_NAME, "where status = #{status}"})    List<Category> findCategory(int status);    @Update({"update", TABLE_NAME ,"set cname=#{cname} where cid=#{cid}"})    void updateCategory(int cid, String cname);    @Select({"select * from", TABLE_NAME, "where cname=#{cname}"})    Category findCategoryByCname(String cname);}