package com.jsp.studyShare.Dao;import ch.qos.logback.classic.joran.action.InsertFromJNDIAction;import com.jsp.studyShare.model.Comment;import org.apache.ibatis.annotations.Insert;import org.apache.ibatis.annotations.Mapper;import org.apache.ibatis.annotations.Select;import org.apache.ibatis.annotations.Update;import org.springframework.stereotype.Repository;import java.util.List;@Mapper@Repository("commentDAO")public interface CommentDao {    String TABLE_NAME = "comment";    String INSERT_FIELD = "tid, openid, comment, commentTime, status";    @Insert({"insert into", TABLE_NAME,"(", INSERT_FIELD, ")values(#{tid},#{openid},#{comment},#{commentTime},#{status})"})    void addComment(Comment comment);    @Select({"select * from", TABLE_NAME, "where tid=#{tid} and status=#{status}"})    List<Comment> getComments(int tid, int status);    @Update({"update",TABLE_NAME,"set status=#{status} where cid=#{cid}"})    void updateStatus(int cid, int status);}