package com.jsp.studyShare.Dao;import com.jsp.studyShare.model.Data;import org.apache.ibatis.annotations.Insert;import org.apache.ibatis.annotations.Mapper;import org.apache.ibatis.annotations.Select;import org.apache.ibatis.annotations.Update;import org.springframework.stereotype.Repository;import java.util.List;import java.util.Set;@Mapper@Repository("dataDAO")public interface DataDao {    String TABLE_NAME = "data";    String INSERT_FIELD = "cid,dataName,dataDesc,dataFile,status,openid,uploadTime,url";    @Insert({"insert into",TABLE_NAME,"(",INSERT_FIELD,")values(#{cid},#{dataName},#{dataDesc},#{dataFile},#{status},#{openid},#{uploadTime},#{url})"})    void insertData(Data data);    @Select({"select * from",TABLE_NAME,"where cid=#{cid} and status=#{status}"})    List<Data> findDataByCid(int cid, int status);    @Select({"select * from",TABLE_NAME,"where status=#{status}"})    List<Data> findAllData(int status);    @Select({"select * from",TABLE_NAME,"where dataName like #{keywords} and status=#{status}"})    List<Data> queryData(String keywords, int status);    @Select({"select * from", TABLE_NAME,"where openid=#{openid} and status=#{status}"})    List<Data> findDataByOpenId(String openid, int status);    @Select({"select * from",TABLE_NAME,"where status=#{status} order by rand() limit 5"})    List<Data> recommend(int status);    @Update({"update",TABLE_NAME,"set status=#{status} where cid=#{cid}"})    void deleDataByCid(int cid,int status);    @Select({"select * from",TABLE_NAME,"where did=#{did} and status=#{status}"})    Data findDataByDid(int did, int status);}