package com.jsp.studyShare.Dao;import ch.qos.logback.classic.db.names.TableName;import com.jsp.studyShare.model.Email;import com.sun.org.apache.bcel.internal.generic.TABLESWITCH;import org.apache.ibatis.annotations.Insert;import org.apache.ibatis.annotations.Mapper;import org.apache.ibatis.annotations.Select;import org.apache.ibatis.annotations.Update;import org.springframework.stereotype.Repository;@Mapper@Repository("emailDAO")public interface EmailDao {    String TABLE_NAME = "email";    String INSERT_FIELD = "email,code,openid";    @Insert({"insert into",TABLE_NAME,"(",INSERT_FIELD,")values(#{email}, #{code},#{openid})"})    void addEmail(Email email);    @Select({"select * from", TABLE_NAME,"where email=#{email}"})    Email selectEmail(String email);    @Update({"update",TABLE_NAME,"set code=#{code} where eid=#{eid}"})    void updateEmail(Email email);    @Update({"update",TABLE_NAME,"set openid=#{openid} where email=#{email}"})    void fixEmail(String openid, String email);}