package com.jsp.studyShare.Dao;import com.jsp.studyShare.model.Reply;import org.apache.ibatis.annotations.Insert;import org.apache.ibatis.annotations.Mapper;import org.apache.ibatis.annotations.Select;import org.apache.ibatis.annotations.Update;import org.springframework.stereotype.Repository;import java.util.List;@Mapper@Repository("replyDAO")public interface ReplyDao {    String TABLE_NAME = "reply";    String INSERT_FIELD = "cid, reply, replyTime ,status";    @Select({"select * from",TABLE_NAME,"where cid=#{cid} and status=1"})    List<Reply> getReply(int cid);    @Insert({"insert into",TABLE_NAME,"(",INSERT_FIELD,")values(#{cid},#{reply},#{replyTime},#{status})"})    void addReply(Reply reply);    @Update({"update",TABLE_NAME,"set userReply=#{userReply},userReplyTime=#{userReplyTime} where rid=#{rid}"})    void addUserReply(Reply reply);    @Update({"update",TABLE_NAME,"set status=#{status} where rid=#{rid}"})    void updateStatus(int rid, int status);}