package com.jsp.studyShare.Dao;import com.jsp.studyShare.model.Token;import org.apache.ibatis.annotations.Insert;import org.apache.ibatis.annotations.Mapper;import org.apache.ibatis.annotations.Select;import org.apache.ibatis.annotations.Update;import org.springframework.stereotype.Repository;@Mapper@Repository("tokenDAO")public interface TokenDao {    String TABLE_NAME = "token";    String INSERT_FIELD = "openid,expired,status,tokens";    @Insert({"insert into",TABLE_NAME,"(",INSERT_FIELD,")values(#{openid},#{expired},#{status},#{tokens})"})    void addToken(Token token);    @Select({"select * from", TABLE_NAME,"where tokens=#{tokens}"})    Token selectByToken(String tokens);    @Update({"update", TABLE_NAME,"set status=#{status} where tokens=#{tokens}"})    void updateStatus(String tokens, int status);}