package com.jsp.studyShare.Dao;import com.jsp.studyShare.model.Topic;import javafx.scene.control.Tab;import org.apache.ibatis.annotations.Insert;import org.apache.ibatis.annotations.Mapper;import org.apache.ibatis.annotations.Select;import org.apache.ibatis.annotations.Update;import org.springframework.stereotype.Repository;import java.util.List;@Mapper@Repository("topicDAO")public interface TopicDao {    String TABLE_NAME = "topic";    String INSERT_FIELD = "openid,subject,sendTime,contents,likeCount,dislikeCount,commentCount,status";    @Insert({"insert into", TABLE_NAME, "(", INSERT_FIELD,")values(#{openid},#{subject},#{sendTime},#{contents},#{likeCount},#{dislikeCount},#{commentCount},#{status})"})    void addTopic(Topic topic);    @Select({"select * from", TABLE_NAME, "where status = 1"})    List<Topic> getTopics();    @Select({"select * from", TABLE_NAME, "where tid=#{tid} and status=1"})    Topic getTopicById(int tid);    @Update({"update", TABLE_NAME, "set status=#{status} where tid=#{tid}"})    void updateStatus(int tid, int status);    @Update({"update", TABLE_NAME, "set commentCount=commentCount+1 where tid=#{tid}"})    void addCommentCount(int tid);    @Update({"update", TABLE_NAME, "set likeCount=#{likeCount},dislikeCount=#{dislikeCount} where tid=#{tid}"})    void updateLikeCount(int tid, int likeCount, int dislikeCount);}