package com.jsp.studyShare.Dao;import com.jsp.studyShare.model.User;import org.apache.ibatis.annotations.Insert;import org.apache.ibatis.annotations.Mapper;import org.apache.ibatis.annotations.Select;import org.apache.ibatis.annotations.Update;import org.springframework.stereotype.Repository;@Mapper@Repository("userDAO")public interface UserDao {    String TABLE_NAME = "user";    String INSERT_FIELD = "openid,username,password,salt,nickname,gender,avatarUrl,country,province,city,status";    @Select({"select * from", TABLE_NAME, "where openid=#{openid}"})    User getUserById(String openid);    @Select({"select * from", TABLE_NAME, "where username=#{username}"})    User getUserByUsername(String username);    @Insert({"insert into", TABLE_NAME,"(",INSERT_FIELD,")values(#{openid},#{username},#{password},#{salt},#{nickname},#{gender}," +            "#{avatarUrl},#{country},#{province},#{city},#{status})"})    void addUser(User user);    @Update({"update",TABLE_NAME,"set uploaded=#{uploaded} where openid=#{openid}"})    void updateUpload(String openid, String uploaded);    @Update({"update",TABLE_NAME,"set downloaded=#{downloaded} where openid=#{openid}"})    void updateDownload(String openid,String downloaded);    @Update({"update",TABLE_NAME,"set collection=#{collection} where openid=#{openid}"})    void updateCollection(String openid, String collection);}