package com.jsp.studyShare.Service;import com.jsp.studyShare.Dao.ReplyDao;import com.jsp.studyShare.model.Reply;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import java.util.*;@Servicepublic class ReplyService {    @Autowired    private ReplyDao replyDAO;    public void addReply(int cid, String reply) {        Reply r = new Reply();        r.setCid(cid);        r.setReply(reply);        r.setReplyTime(new Date());        r.setStatus(1);        replyDAO.addReply(r);    }    public void addUserReply(int rid, String userReply) {        Reply r = new Reply();        r.setRid(rid);        r.setUserReply(userReply);        r.setUserReplyTime(new Date());        replyDAO.addUserReply(r);    }    public void deleReply(int rid) {        replyDAO.updateStatus(rid, 0);    }}