package com.jsp.studyShare.model;import java.util.Date;public class Comment {    private int cid;    private int tid;    private String openid;    private String comment;    private Date commentTime;    private int status;    public int getCid() {        return cid;    }    public void setCid(int cid) {        this.cid = cid;    }    public int getTid() {        return tid;    }    public void setTid(int tid) {        this.tid = tid;    }    public String getOpenid() {        return openid;    }    public void setOpenid(String openid) {        this.openid = openid;    }    public String getComment() {        return comment;    }    public void setComment(String comment) {        this.comment = comment;    }    public Date getCommentTime() {        return commentTime;    }    public void setCommentTime(Date commentTime) {        this.commentTime = commentTime;    }    public int getStatus() {        return status;    }    public void setStatus(int status) {        this.status = status;    }}