package com.jsp.studyShare.model;import java.util.Date;public class Reply {    private int rid;    private int cid;    private String reply;    private int status;    private Date replyTime;    private String userReply;    private Date userReplyTime;    public int getRid() {        return rid;    }    public void setRid(int rid) {        this.rid = rid;    }    public int getCid() {        return cid;    }    public void setCid(int cid) {        this.cid = cid;    }    public String getReply() {        return reply;    }    public void setReply(String reply) {        this.reply = reply;    }    public int getStatus() {        return status;    }    public void setStatus(int status) {        this.status = status;    }    public Date getReplyTime() {        return replyTime;    }    public void setReplyTime(Date replyTime) {        this.replyTime = replyTime;    }    public String getUserReply() {        return userReply;    }    public void setUserReply(String userReply) {        this.userReply = userReply;    }    public Date getUserReplyTime() {        return userReplyTime;    }    public void setUserReplyTime(Date userReplyTime) {        this.userReplyTime = userReplyTime;    }}