package com.jsp.studyShare.model;import java.util.Date;public class ResponseTopic {    private int tid;    private String subject;    private Date sentTime;    private String contents;    private String nickname;    private int likeCount;    private int dislikeCount;    private int commentCount;    public int getTid() {        return tid;    }    public void setTid(int tid) {        this.tid = tid;    }    public String getSubject() {        return subject;    }    public void setSubject(String subject) {        this.subject = subject;    }    public Date getSentTime() {        return sentTime;    }    public void setSentTime(Date sentTime) {        this.sentTime = sentTime;    }    public String getContents() {        return contents;    }    public void setContents(String contents) {        this.contents = contents;    }    public String getNickname() {        return nickname;    }    public void setNickname(String nickname) {        this.nickname = nickname;    }    public int getLikeCount() {        return likeCount;    }    public void setLikeCount(int likeCount) {        this.likeCount = likeCount;    }    public int getDislikeCount() {        return dislikeCount;    }    public void setDislikeCount(int dislikeCount) {        this.dislikeCount = dislikeCount;    }    public int getCommentCount() {        return commentCount;    }    public void setCommentCount(int commentCount) {        this.commentCount = commentCount;    }}