package com.jsp.studyShare.model;import java.util.Date;public class Topic {    private int tid;    private String openid;    private String subject;    private Date sendTime;    private String contents;    private int likeCount;    private int dislikeCount;    private int commentCount;    private int status;    public int getTid() {        return tid;    }    public void setTid(int tid) {        this.tid = tid;    }    public String getOpenid() {        return openid;    }    public void setOpenid(String openid) {        this.openid = openid;    }    public String getSubject() {        return subject;    }    public void setSubject(String subject) {        this.subject = subject;    }    public Date getSendTime() {        return sendTime;    }    public void setSentTime(Date sendTime) {        this.sendTime = sendTime;    }    public String getContents() {        return contents;    }    public void setContents(String contents) {        this.contents = contents;    }    public int getLikeCount() {        return likeCount;    }    public void setLikeCount(int likeCount) {        this.likeCount = likeCount;    }    public int getDislikeCount() {        return dislikeCount;    }    public void setDislikeCount(int dislikeCount) {        this.dislikeCount = dislikeCount;    }    public int getCommentCount() {        return commentCount;    }    public void setCommentCount(int commentCount) {        this.commentCount = commentCount;    }    public int getStatus() {        return status;    }    public void setStatus(int status) {        this.status = status;    }}