package com.jsp.studyShare.utils;public class RedisKeyUtils {    private static String SPLIT = ":";    private static String BIZ_LIKE = "LIKE";    private static String BIZ_DISLIKE = "DISLIKE";    /**     * 产生key。 例如entityId为2，entityType=Like，则产生的key是LIKE:ENTITY_SHARE:2     * @param entityId     * @return     */    public static String getLikeKey(int entityId) {        return BIZ_LIKE  + SPLIT + String.valueOf(entityId);    }    /**     * 取消点赞  例如DISLIKE:ENTITY_SHARE:2     * @param entityId     * @return     */    public static String getDisLikeKey(int entityId) {        return BIZ_DISLIKE + SPLIT + String.valueOf(entityId);    }}